import './App.css';
import {BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './pages/Home'
import EditPost from './pages/EditPost'
import PostForm from './pages/PostForm'
import Login from './pages/Login'
import Register from './pages/Register'
import NavBar from './components/NavBar'

function App() {
  return (
    <div className='min-h-screen bg-gray-50 text-gray-800 fron-sans'>
      <Router>
        {/* Navbar is always visible */}
        <NavBar />

        {/* Page content */}
        <main>
          <Routes>
            <Route path = "/" element = {<Home />} />
            <Route path = "/post/new" element = {<PostForm />} />
            <Route path = "/login" element = {<Login />} />
            <Route path = "/register" element = {<Register />} />
            <Route path="/post/post/:id" element={<EditPost />} />
          </Routes>
        </main>

        {/* Footer */}
        <footer className="bg-white text-sm text-center text-gray-500 py-4 border-t">
          © 2025 Kevin Ren · Powered by AWS & Docker
        </footer>
      </Router>
    </div>
  );
}

export default App;
