import { useEffect, useState } from "react";
import axiosClient from "../api/axiosClient";
import { useNavigate, Link } from "react-router-dom";


const Home = () =>{
  const navigate = useNavigate();
  const [posts, setPosts] = useState([]);

  const fetchPost = async () => {
    try {
      const res = await axiosClient.get("/post/posts")
      setPosts(res.data);
    } catch {
      alert("Failed to load posts")
    }
  };


  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  }

  const handleDelete = async (postId) => {
    if (!window.confirm("Are you sure you want to delete this post?")) return;

    try {
      await axiosClient.delete(`/post/delete/${postId}`);
      alert("Post Deleted");
      fetchPost();
    } catch (err) {
      alert("Failed to delete post");
    }
  };

  return(
    <div className="px-4 py-8 max-w-4xl mx-auto"> 
      {/* Hero Section */}
      <section>
        <h1 className="text-4xl font-bold text-blue-600 mb-4">Full Stack User Management System</h1>
        <p className="text-gray-600 mb-6">
        Built with React, ASP.NET Core, MySQL, Docker, ECS Fargate, and S3. JWT-secured authentication and cloud-native CI/CD pipeline.
        </p>
        <div className="space-x-4">
          <Link to="/register" className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 transition">Get Started</Link>
          <Link to="/post/new" className="border border-blue-600 text-blue-600 px-6 py-2 rounded hover:bg-blue-50 transition">Create Post</Link>
        </div>
      </section>
      <h2>All Posts</h2>
      <button onClick={handleLogout}>Logout</button>
      {posts.length === 0 && <p>No posts found.</p>}
      <ul>
        {posts.map(post => (
          <li key={post.postId}>
            <h3>{post.postTitle}</h3>
            <p>{post.postContent}</p>
            <small>Posted on: {new Date(post.postCreated).toLocaleString()}</small>
            <button onClick={() => handleDelete(post.postId)}>Delete</button>
            <button onClick={() => navigate(`/post/post/${post.postId}`)}>Edit</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
